<span class="line"><span style="color:#50FA7B;">$</span><span style="color:#F8F8F2;"> </span><span style="color:#F1FA8C;">sudo</span><span style="color:#F8F8F2;"> </span><span style="color:#F1FA8C;">ufw</span><span style="color:#F8F8F2;"> </span><span style="color:#F1FA8C;">allow</span><span style="color:#F8F8F2;"> </span><span style="color:#F1FA8C;">ssh</span></span>
